<style>	.pac-container {		z-index: 100000 !important;	}	#tbListDescriptions th, #tbListDescriptions td {		font-size: 0.9em;	}</style><button class="btn btn-primary" id="btnModalAddDescription">	<i class="fas fa-clock"></i>	Nueva descripción de producto</button><hr><div class="table-responsive">	<table class="table table-hover table-striped" id="tbListDescriptions" width="100%">		<thead>		<tr>			<th></th>			<th>Imagen</th>			<th>Tipo de fresa</th>			<th>Fresas</th>			<th>Fresas café</th>			<th>Fresas blancas</th>			<th>Rosas</th>			<th>Espirales</th>			<th>Puntos</th>			<th>Rayas</th>			<th>Figuras</th>			<th>Lisa</th>			<th>Chocolate</th>			<th>Desnuda/Rayada</th>			<th>Rosa chocolate</th>			<th>Palo chico</th>			<th>Palo grande</th>		</tr>		</thead>		<tbody></tbody>	</table></div><!-- Modal add delivery time--><div class="modal fade" id="modalProductDescription" tabindex="-1" role="dialog" aria-labelledby="addProductDescriptionLabel" aria-hidden="true">	<div class="modal-dialog modal-lg" role="document">		<div class="modal-content">			<div class="modal-header">				<h5 class="modal-title" id="addProductDescriptionLabel">Descripción del producto</h5>				<button type="button" class="close" data-dismiss="modal" aria-label="Close">					<span aria-hidden="true">&times;</span>				</button>			</div>			<div class="modal-body">				<form id="formAddProductDescription">					<input type="hidden" id="descriptionId" value="-1" class="form-control" data-binding-field="id_descripcion" />					<div class="row">						<div class="col-xs-12 col-md-4">							<div>								<img id="tImage" src="" class="w-100">							</div>						</div>						<div class="col-xs-12 col-md-8">							<div class="form-group row">								<label for="tStrawberry" class="col-sm-4 col-form-label">Tipo fresas:</label>								<div class="col-sm-8">									<select id="tStrawberry" class="form-control" data-binding-field="t_fresa">										<option value="normal">Normal</option>										<option value="chica">Chica</option>										<option value="grande">Grande</option>										<option value="acostada">Acostada</option>									</select>								</div>							</div>							<div class="form-group row">								<label for="nStrawberry" class="col-sm-4 col-form-label">No. fresas:</label>								<div class="col-sm-8">									<input type="text" id="nStrawberry" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="no_fresas" />								</div>							</div>							<div class="form-group row">								<label for="nStrawberryBrown" class="col-sm-4 col-form-label">No. fresas café:</label>								<div class="col-sm-8">									<input type="text" id="nStrawberryBrown" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="no_fresas_cafe" />								</div>							</div>							<div class="form-group row">								<label for="nStrawberryWhite" class="col-sm-4 col-form-label">No. fresas blancas:</label>								<div class="col-sm-8">									<input type="text" id="nStrawberryWhite" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="no_fresas_blanca" />								</div>							</div>							<div class="form-group row">								<label for="nRoses" class="col-sm-4 col-form-label">No. rosas:</label>								<div class="col-sm-8">									<input type="text" id="nRoses" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="no_rosas" />								</div>							</div>							<div class="form-group row">								<label for="nSpirals" class="col-sm-4 col-form-label">No. espirales:</label>								<div class="col-sm-8">									<input type="text" id="nSpirals" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="espirales" />								</div>							</div>							<div class="form-group row">								<label for="nDots" class="col-sm-4 col-form-label">No. puntos:</label>								<div class="col-sm-8">									<input type="text" id="nDots" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="puntos" />								</div>							</div>							<div class="form-group row">								<label for="nStripes" class="col-sm-4 col-form-label">No. rayas:</label>								<div class="col-sm-8">									<input type="text" id="nStripes" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="rayas" />								</div>							</div>							<div class="form-group row">								<label for="nFigures" class="col-sm-4 col-form-label">No. figuras:</label>								<div class="col-sm-8">									<input type="text" id="nFigures" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="figuras" />								</div>							</div>							<div class="form-group row">								<label for="nFlats" class="col-sm-4 col-form-label">No. lisas:</label>								<div class="col-sm-8">									<input type="text" id="nFlats" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="lisa" />								</div>							</div>							<div class="form-group row">								<label for="nChocolate" class="col-sm-4 col-form-label">No. chocolates:</label>								<div class="col-sm-8">									<input type="text" id="nChocolate" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="s_chocolate" />								</div>							</div>							<div class="form-group row">								<label for="nNakedStripes" class="col-sm-4 col-form-label">No. desnuda/rayada:</label>								<div class="col-sm-8">									<input type="text" id="nNakedStripes" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="desnuda_rayada" />								</div>							</div>							<div class="form-group row">								<label for="nChocolateRose" class="col-sm-4 col-form-label">No. rosas de chocolate:</label>								<div class="col-sm-8">									<input type="text" id="nChocolateRose" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="rosa_de_chocolate" />								</div>							</div>							<div class="form-group row">								<label for="nSmallStick" class="col-sm-4 col-form-label">No. palos chicos:</label>								<div class="col-sm-8">									<input type="text" id="nSmallStick" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="palo_chico" />								</div>							</div>							<div class="form-group row">								<label for="nBigStick" class="col-sm-4 col-form-label">No. palos grandes:</label>								<div class="col-sm-8">									<input type="text" id="nBigStick" class="form-control plg-numeric" maxlength="3" value="0" data-binding-field="palo_grande" />								</div>							</div>						</div>					</div>					<div>						<button type="submit" class="btn btn-success btn-block">Guardar</button>					</div>				</form>			</div>		</div>	</div></div>